    private void btCalculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btCalculoActionPerformed
        Double nota1 = Double.valueOf(tfNota1.getText());
        Double nota2 = Double.valueOf(tfNota2.getText());
        
        Double soma = nota1 + nota2;
        
        lbResultado.setText(
                String.format("Soma: %.2f", soma));
        
        Integer faltas = Integer.valueOf(tfFaltas.getText()) ;
        
        lbResultado.setText(lbResultado.getText() +
        String.format(" - Faltas: %d", faltas));
        
        lbResultadosLindos.setText(String.format(
                "<html> Soma: %.2f <br>" +
                        "Faltas: %d </html>"
                ,soma, faltas));
        
    }//GEN-LAST:event_btCalculoActionPerformed

